import React from "react";
import { View, Text, ScrollView, TouchableOpacity, Image } from "react-native";

export default class ReadingScreen extends React.Component {
  constructor(data) {
    super();
    this.state = {
      articleDetails: data.route.params.details,
    };
  }
  render() {
    var { articleDetails } = this.state;
    const { text, title, url } = articleDetails;
    console.log(this.state.articleDetails);
    return (
      <ScrollView style={{ margin: 20 }}>
        <View>
          <Text style={{ fontWeight: "bold", fontSize: 18 }}>Title:</Text>
          <Text style={{ fontStyle: "italic" }}>
            {"\t"}
            {title}
          </Text>
        </View>
        <View style={{width: 20, height:30, alignItems }}>
          <TouchableOpacity>
            <Image source={require("../assets/hear.png")} />
          </TouchableOpacity>
        </View>
        <View style={{ marginTop: 20 }}>
          <Text style={{ fontWeight: "bold", fontSize: 18 }}>Text:</Text>
          <Text style={{ fontStyle: "italic", fontFamily: "sans-serif-condensed" }}>
            {"\t"}
            {text}
          </Text>
        </View>
      </ScrollView>
    );
  }
}
