import React, { Component } from "react";
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
} from "react-native";
import { Header, AirbnbRating, Icon } from "react-native-elements";
import { RFValue } from "react-native-responsive-fontsize";
import axios from "axios";
import { shadow } from "react-native-paper";

export default class HomeScreen extends React.Component {
  constructor() {
    super();
    this.state = {
      articleDetails: { "Loading...": "" },
      articleDetailsURLWeb:
        "http://cors-anywhere.herokuapp.com/http://2ce6-49-37-36-88.ngrok.io",
      articleDetailsURLMob: "https://2ce6-49-37-36-88.ngrok.io",
    };
  }

  getArticles = () => {
    var { articleDetailsURLMob, articleDetailsURLWeb } = this.state;
    var url = articleDetailsURLWeb;
    console.log(url);
    axios
      .get(url)
      .then((response) => {
        let details = response.data.data;
        this.setState({ articleDetails: details });
        c;
      })
      .catch((error) => {
        console.log(error.message);
      });
  };

  componentDidMount() {
    this.getArticles();
  }

  render() {
    var { articleDetails } = this.state;
    const { text, title } = articleDetails;
    console.log(title);

    return (
      <ScrollView style={{ flex: 1 }}>
        <View style={styles.headerContainer}>
          <Header
            centerComponent={{
              text: "Articles Recommended",
              style: styles.headerTitle,
            }}
            rightComponent={{ icon: "search", color: "#fff" }}
            backgroundColor={"#d500f9"}
            containerStyle={{ flex: 1 }}
          />
        </View>
        <View style={styles.TopContainer}>
          <View style={styles.display}>
            <View title="Title" style={{ margin: RFValue(10) }}>
              <Text style={{ fontWeight: "bold" }}>
                <Text style={{ fontSize: 17 }}>Title:</Text>
                {"\n"}
                <Text>{"\t\t\t\t"}</Text>
                <Text>{title}</Text>
              </Text>
            </View>
            <View style={{ marginTop: RFValue(10) }}>
              <View style={{ margin: RFValue(10) }}>
                <Text numberOfLines={10}>
                  <Text style={{ fontWeight: "bold", fontSize: 17 }}>
                    Text:
                  </Text>{" "}
                  {"\n"}
                  <Text>{"\t\t\t\t"}</Text>
                  <Text>{text}</Text>
                </Text>
                <Text> </Text>
              </View>
              <View style={{ alignItems: "center" }}>
                <TouchableOpacity
                  onPress={() => {
                    navigation.navigate("Reading");
                  }}
                >
                  <Text>Continoue Reading...</Text>
                </TouchableOpacity>
              </View>
            </View>
          </View>
        </View>
        <View style={styles.BottomContainer}>
          <Text>5</Text>
        </View>
      </ScrollView>
    );
  }
}

const styles = StyleSheet.create({
  TopContainer: {
    backgroundColor: "#f8f9be",
  },

  headerContainer: {
    flex: 0.1,
  },
  headerTitle: {
    color: "#fff",
    fontWeight: "bold",
    fontSize: RFValue(20),
  },

  display: {
    margin: RFValue(15),
    borderWidth: 1,
    borderRadius: 15,
    backgroundColor: "white",
    shadowColor: "black",
  },

  BottomContainer: {
    backgroundColor: "red",
  },
});
