import React from "react";
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  Image,
  Dimensions,
} from "react-native";
import * as Speech from "expo-speech";

export default class ReadingScreen extends React.Component {
  constructor(data) {
    super();
    this.state = {
      articleDetails: data.route.params.details,
      windowWidth: Dimensions.get("window").width,
      text: data.route.params.details.text,
    };
  }

  speech = () => {
    var Say = this.state.text;
    Speech.speak(Say);
  };

  render() {
    var { articleDetails } = this.state;
    const { text, title, url } = articleDetails;
    console.log(this.state.articleDetails);
    return (
      <ScrollView style={{ margin: 20 }}>
        <View>
          <Text style={{ fontWeight: "bold", fontSize: 18 }}>Title:</Text>
          <Text style={{ fontStyle: "italic" }}>
            {"\t"}
            {title}
          </Text>
        </View>
        <View style={{ width: "100%", height: 30, alignContent: "flex-end" }}>
          <TouchableOpacity
            style={{ alignContent: "flex-end" }}
            onPress={() => {this.state.speech}}
          >
            <Image source={require("../assets/hear.png")} />
          </TouchableOpacity>
        </View>
        <View style={{ marginTop: 20 }}>
          <Text style={{ fontWeight: "bold", fontSize: 18 }}>Text:</Text>
          <Text
            style={{ fontStyle: "italic", fontFamily: "sans-serif-condensed" }}
          >
            {"\t"}
            {text}
          </Text>
        </View>
      </ScrollView>
    );
  }
}
